package ControlStatements.BhoomikaAssesments;

public class Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 15, b = 3;
		System.out.println(a+=b);
		System.out.println(a-=b);
		System.out.println(a*=b);
		System.out.println(a/=b);
		System.out.println(a%=b);

	}

}
