package ControlStatements.BhoomikaAssesments;

public class Logical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5, b = 5, c = 10;
		System.out.println(a < c && b < c);
		System.out.println(a > c && b < c);
		System.out.println(a < c || b < c);
		System.out.println(a > c || b < c);
	}

}
