package ControlStatements.BhoomikaAssesments;

public class Unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10, b, c, d, e;
		b = (--i);
		c = (++i);
		d = (i + 1);
		e = (i++);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
	}

}
